"use client";

import { StarFilledIcon } from "@radix-ui/react-icons";
import { useEffect, useState } from "react";

import { RaisedButton } from "@/registry/new-york/ui/raised-button";

interface GitHubRepo {
  stargazers_count: number;
  html_url: string;
  name: string;
  full_name: string;
}

interface GitHubStarsButtonProps {
  repo: string;
  showLabel?: boolean;
  size?: "sm" | "default" | "lg";
  className?: string;
}

export function GitHubStarsButton({
  repo,
  showLabel = true,
  size = "sm",
  className,
}: GitHubStarsButtonProps) {
  const [starCount, setStarCount] = useState<number | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    let isMounted = true;

    async function fetchStars() {
      try {
        const response = await fetch(`https://api.github.com/repos/${repo}`);

        if (!response.ok) {
          throw new Error("Failed to fetch repository data");
        }

        const data: GitHubRepo = await response.json();

        if (isMounted) {
          setStarCount(data.stargazers_count);
          setIsLoading(false);
        }
      } catch (error) {
        console.error("Error fetching GitHub stars:", error);
        if (isMounted) {
          setIsLoading(false);
        }
      }
    }

    fetchStars();

    return () => {
      isMounted = false;
    };
  }, [repo]);

  return (
    <a
      href={`https://github.com/${repo}`}
      target="_blank"
      rel="noopener noreferrer"
    >
      <RaisedButton
        size={size}
        className={`group rounded-xl border-0! ${className || ""}`}
        color="#1c1c1c"
      >
        <div className="flex items-center">
          <svg
            className="mr-1 size-4 fill-white"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path d="M12 0c-6.626 0-12 5.373-12 12 0 5.302 3.438 9.8 8.207 11.387.599.111.793-.261.793-.577v-2.234c-3.338.726-4.033-1.416-4.033-1.416-.546-1.387-1.333-1.756-1.333-1.756-1.089-.745.083-.729.083-.729 1.205.084 1.839 1.237 1.839 1.237 1.07 1.834 2.807 1.304 3.492.997.107-.775.418-1.305.762-1.604-2.665-.305-5.467-1.334-5.467-5.931 0-1.311.469-2.381 1.236-3.221-.124-.303-.535-1.524.117-3.176 0 0 1.008-.322 3.301 1.23.957-.266 1.983-.399 3.003-.404 1.02.005 2.047.138 3.006.404 2.291-1.552 3.297-1.23 3.297-1.23.653 1.653.242 2.874.118 3.176.77.84 1.235 1.911 1.235 3.221 0 4.609-2.807 5.624-5.479 5.921.43.372.823 1.102.823 2.222v3.293c0 .319.192.694.801.576 4.765-1.589 8.199-6.086 8.199-11.386 0-6.627-5.373-12-12-12z" />
          </svg>
          {showLabel && <span className="ml-1">GitHub</span>}
        </div>
        <div className="flex items-center gap-1 text-sm">
          <StarFilledIcon className="relative top-px size-4 text-white group-hover:text-yellow-300" />
          <span className="font-medium text-white">
            {isLoading ? "..." : starCount || "0"}
          </span>
        </div>
      </RaisedButton>
    </a>
  );
}
